Bash(curl -s -H "Accept: application/sparql-results+json" --user admin:admin123 "http://localhost:3030/semem/query" --data-urlencode "query=PREFIX bm: <http://purl.org/stuff/bm/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
SELECT ?target ?contentType
FROM <http://hyperdata.it/content>
WHERE {
  ?bookmark a bm:Bookmark ;
            bm:target ?target ;
            bm:status ?status .
  OPTIONAL { ?bookmark bm:contentType ?contentType }
  FILTER (?status = ""200""^^xsd:integer)
  FILTER (CONTAINS(LCASE(STR(?contentType)), ""text/html""))
}
LIMIT 5")

