Bash(curl -s "http://localhost:3030/newsmonitor/query" --data-urlencode "query=PREFIX sioc: <http://rdfs.org/sioc/ns#>
PREFIX dc: <http://purl.org/dc/elements/1.1/>
SELECT ?title ?feedTitle
WHERE {
  GRAPH <http://hyperdata.it/content> {
    ?post a sioc:Post ;
          dc:title ?title ;
          sioc:has_container ?feed ;
          dc:date ?date .
  }
  GRAPH <http://hyperdata.it/feeds> {
    ?feed dc:title ?feedTitle .
  }
}
ORDER BY DESC(?date)
LIMIT 10" -u admin:admin123)

